require 'optparse'

$options = {}
OptionParser.new do |opts|
  opts.banner = 'Spawn a listener for a given kite'
  opts.on('-u', '--kite-uri U')   {|u| $options[:kite_uri]  = u}
  opts.on('-k', '--key K')        {|k| $options[:key]       = k}
  opts.on('-s', '--secret S')     {|s| $options[:secret]    = s}
  opts.on('-l', '--logfile L')    {|l| $options[:logfile]   = l}
  opts.on('-n', '--kite-name N')  {|n| $options[:kite_name] = n}
  opts.on('-j', '--joined-channels-path J') do |j|
    $options[:joined_channels_path] = j
  end
end.parse!

